style.css:
body {
    font-family: Arial, sans-serif;
    margin: 0;
    padding: 0;
    background-color: #fff8e1;
}
header {
    background-color: #ffeb3b;
    padding: 20px;
    text-align: center;
}
nav ul {
    list-style-type: none;
    padding: 0;
}
nav ul li {
    display: inline;
    margin: 0 15px;
}
nav ul li a {
    text-decoration: none;
    color: #000;
}
nav ul li a:hover, .active {
    text-decoration: underline;
}
main {
    padding: 20px;
    text-align: center;
}
.product {
    border: 1px solid #ccc;
    padding: 15px;
    margin: 10px;
    display: inline-block;
    text-align: left;
}
.product img {
    max-width: 100%;
    height: auto;
}
.btn {
    background-color: #ffeb3b;
    padding: 10px 20px;
    text-decoration: none;
    color: #000;
}
.btn:hover {
    background-color: #fbc02d;
}
footer {
    text-align: center;
    padding: 10px;
    background-color: #ffd54f;
    position: relative;
    bottom: 0;
    width: 100%;
}
form {
    max-width: 400px;
    margin: auto;
}
label {
    display: block;
    margin: 10px 0 5px;
}
input, textarea {
    width: 100%;
    padding: 10px;
    margin-bottom: 10px;
}
button {
    background-color: #ffeb3b;
    padding: 10px 15px;
    border: none;
    cursor: pointer;
}
button:hover {
    background-color: #fbc02d;
}
@media (max-width: 600px) {
    .product {
        width: 100%;
        box-sizing: border-box;
    }
    nav ul li {
        display: block;
        margin: 5px 0;
    }
}

script.js:
let cart = [];

function addToCart(product, quantity) {
    if (quantity < 1 || quantity > 10) {
        alert('Please select a valid quantity.');
        return;
    }
    const cartItem = cart.find(item => item.product === product);
    if (cartItem) {
        cartItem.quantity += quantity;
    } else {
        cart.push(( product, quantity ));
    }
    saveCart();
    displayCart();
    alert(`${product} has been added to your cart!`);
}

function displayCart() {
    const cartDiv = document.getElementById('cart') || document.getElementById('cartItems');
    const totalPriceDiv = document.getElementById('totalPrice');
    cartDiv.innerHTML = '';
    let totalPrice = 0;
    cart.forEach((item, index) => {
        cartDiv.innerHTML += `<p>${item.product} - Quantity: ${item.quantity} <button onclick="removeFromCart(${index})">Remove</button></p>`;
        if (item.product === 'Organic Bananas') totalPrice += item.quantity * 1.00;
        if (item.product === 'Golden Bananas') totalPrice += item.quantity * 0.80;
    });
    totalPriceDiv.innerHTML = `<strong>Total Price: $${totalPrice.toFixed(2)}</strong>`;
}

function removeFromCart(index) {
    cart.splice(index, 1);
    saveCart();
    displayCart();
}

function proceedToCheckout() {
    window.location.href = 'checkout.html';
}

function saveCart() {
    localStorage.setItem('cart', JSON.stringify(cart));
}

function loadCart() {
    const savedCart = localStorage.getItem('cart');
    if (savedCart) {
        cart = JSON.parse(savedCart);
        displayCart();
    }
}

window.onload = loadCart;

var contactForm = document.getElementById('contactForm');
if (contactForm) {
    contactForm.addEventListener('submit', function(event) {
        event.preventDefault();
        const emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        const namePattern = /^[a-zA-Z ]+$/;
        if (!emailPattern.test(this.email.value)) {
            alert('Please enter a valid email address.');
            return;
        }
        if (!namePattern.test(this.name.value)) {
            alert('Please enter a valid name without special characters.');
            return;
        }
        if (!this.name.value || !this.message.value || this.message.value.length < 10) {
            alert('Please fill in all fields and ensure the message is at least 10 characters long.');
            return;
        }
        alert('Thank you for contacting us!');
        this.reset();
    });
}
